<template>
  <div>
    <div class="titulo">
      <div>
        <Tag
          v-if="!titulo"
          class="tag-titulo"
          icon="pi pi-info-circle"
          severity="info"
          value="Ingrese título"
        ></Tag>
      </div>
      <InputText id="titulo" v-model="titulo" />
    </div>

    <Tag
      v-if="!isTexto && !isImagen && !isVideo"
      class="tag"
      icon="pi pi-info-circle"
      severity="info"
      value="Elija al menos un componente de la noticia"
    ></Tag>

    <div class="noticias-admin">
      <NoticiaTexto
        class="componente"
        ref="notiTexto"
        v-if="isTexto"
        :texto="texto"
        @quitar="quitarTexto"
      />

      <NoticiaImagen
        class="componente"
        ref="notiImagen"
        v-if="isImagen"
        :img="imagen"
        :guardar="guardar"
        @quitar="quitarImagen"
      />

      <NoticiaVideo
        class="componente"
        ref="notiVideo"
        v-if="isVideo"
        :video="video"
        :guardar="guardar"
        @quitar="quitarVideo"
      />

      <div class="opciones">
        <div
          id="agregar-texto"
          :class="isTexto ? 'agregado' : 'agregar'"
          @click="addComponenteTexto()"
        >
          <img :src="addTextIcon" />
        </div>

        <div
          id="agregar-imagen"
          :class="isImagen ? 'agregado' : 'agregar'"
          @click="addComponenteImagen()"
        >
          <img :src="addImageIcon" />
        </div>

        <div
          id="agregar-video"
          :class="isVideo ? 'agregado' : 'agregar'"
          @click="addComponenteVideo()"
        >
          <img :src="addVideoIcon" />
        </div>
      </div>
      <div class="boton">
        <Button icon="pi pi-eraser" label="Limpiar" @click="limpiar" raised />
        <Button
          v-if="!editar"
          icon="pi pi-check"
          @click="guardarNoticia"
          label="Guardar"
          severity="success"
          raised
        />
        <Button
          v-else
          icon="pi pi-refresh"
          @click="actualizarNoicia"
          label="Actualizar"
          severity="success"
          raised
        />
      </div>
    </div>
  </div>
</template>

<script>
import NoticiaTexto from "./NoticiaTexto.vue";
import NoticiaImagen from "./NoticiaImagen.vue";
import NoticiaVideo from "./NoticiaVideo.vue";
import Tag from "primevue/tag";
import InputText from "primevue/inputtext";
import Button from "primevue/button";
import NoticiaLecturaVue from "./NoticiaLectura.vue";

//Dialogo
import { useToast } from "primevue/usetoast";
import { useConfirm } from "primevue/useconfirm";

//Helpers
import {
  ingresarNoticiaFachada,
  actualizarNoticiaFachada,
} from "@/modules/publicacion/helpers/NoticiaCliente";
import { obtenerFecha } from "@/helpers/Funciones";

export default {
  components: {
    NoticiaTexto,
    NoticiaImagen,
    NoticiaVideo,
    NoticiaLecturaVue,
    InputText,
    Button,
    Tag,
  },
  data() {
    return {
      toast: useToast(),
      confirm: useConfirm(),
      //Iconos de opciones
      addTextIcon:
        "data:image/png;base64,",
      addImageIcon:
        "data:image/png;base64,",
      addVideoIcon:
        "data:image/png;base64,",

      //If para crear componentes
      isTexto: false,
      isImagen: false,
      isVideo: false,

      titulo: "",

      texto: "",
      imagen: "",
      video: "",

      editar: false,

      //Datos obtenidos de los componentes
      textoObtenido: null,
      imagenObtenido: null,
      videoObtenido: null,
    };
  },

  props: {
    //Noticia traida para editar
    noticia: {
      type: Object,
      required: true,
    },
  },

  mounted() {
    if (this.noticia.titulo) {
      this.editar = true;
      this.titulo = this.noticia.titulo;

      if (this.noticia.texto) {
        this.isTexto = true;
        this.texto = this.noticia.texto;
      }

      if (this.noticia.imagen) {
        this.isImagen = true;
        this.imagen = this.noticia.imagen;
      }

      if (this.noticia.video) {
        this.isVideo = true;
        this.video = this.noticia.video;
      }
    }
  },

  methods: {
    addComponenteTexto() {
      this.isTexto = true;
    },
    addComponenteImagen() {
      this.isImagen = true;
    },
    addComponenteVideo() {
      this.isVideo = true;
    },
    quitarTexto() {
      this.confirm.require({
        message: "Se eliminará el componente",
        header: "Confirmación de Eliminación",
        icon: "pi pi-exclamation-triangle",
        acceptClass: "p-button-danger",
        acceptLabel: "Sí",
        rejectLabel: "No",
        accept: () => {
          this.texto = "";
          this.isTexto = false;
        },
      });
    },
    quitarImagen() {
      this.confirm.require({
        message: "Se eliminará el componente",
        header: "Confirmación de Eliminación",
        icon: "pi pi-exclamation-triangle",
        acceptClass: "p-button-danger",
        acceptLabel: "Sí",
        rejectLabel: "No",
        accept: () => {
          this.imagen = "";
          this.isImagen = false;
        },
      });
    },
    quitarVideo() {
      this.confirm.require({
        message: "Se eliminará el componente",
        header: "Confirmación de Eliminación",
        icon: "pi pi-exclamation-triangle",
        acceptClass: "p-button-danger",
        acceptLabel: "Sí",
        rejectLabel: "No",
        accept: () => {
          this.video = "";
          this.isVideo = false;
        },
      });
    },

    limpiar() {
      this.confirm.require({
        message: "Seguro que desea limpiar los campos de la noticia",
        header: "Se limpiaran los campos",
        icon: "pi pi-exclamation-triangle",
        acceptClass: "p-button-danger",
        acceptLabel: "Sí",
        rejectLabel: "No",
        accept: () => {
          this.titulo = "";
          this.isTexto = false;
          this.isImagen = false;
          this.isVideo = false;
        },
      });
    },

    obtenerDatosComponentes() {
      const notiTexto = this.$refs.notiTexto;
      let countComponentes = 0;
      if (notiTexto) {
        if (notiTexto.value) {
          countComponentes++;
          this.textoObtenido = notiTexto.value;
        } else {
          this.toast.add({
            severity: "warn",
            summary: "Advertencia",
            detail: "Ingrese texto o elimine el componente",
            life: 3000,
          });
          return false;
        }
      }
      const notiImagen = this.$refs.notiImagen;
      if (notiImagen) {
        if (notiImagen.imagen) {
          countComponentes++;
          this.imagenObtenido = notiImagen.imagen;
        } else {
          this.toast.add({
            severity: "warn",
            summary: "Advertencia",
            detail: "Ingrese imagen o elimine el componente",
            life: 3000,
          });
          return false;
        }
      }
      const notiVideo = this.$refs.notiVideo;
      if (notiVideo) {
        if (notiVideo.urlEmbed) {
          countComponentes++;
          this.videoObtenido = notiVideo.urlEmbed;
        } else {
          this.toast.add({
            severity: "warn",
            summary: "Advertencia",
            detail: "Ingrese video o elimine el componente",
            life: 3000,
          });
          return false;
        }
      }

      if (countComponentes < 1) {
        this.toast.add({
          severity: "warn",
          summary: "Advertencia",
          detail: "Se debe ingresar al menos un componente",
          life: 3000,
        });
        return false;
      } else {
        return true;
      }
    },

    guardarNoticia() {
      if (this.titulo) {
        if (this.obtenerDatosComponentes()) {
          const ntc = {
            titulo: this.titulo,
            texto: this.textoObtenido,
            imagen: this.imagenObtenido,
            video: this.videoObtenido,
            fechaPublicacion: obtenerFecha(),
          };
          try {
            ingresarNoticiaFachada(ntc);
            this.toast.add({
              severity: "success",
              summary: "Creado",
              detail: "Se ingresó la noticia",
              life: 1000,
            });
            setTimeout(() => {
              window.location.reload();
            },1200);
          } catch (error) {
            console.log("No se pudo guardar la noticia: ");
          }
        }
      } else {
        this.toast.add({
          severity: "warn",
          summary: "Advertencia",
          detail: "Se debe ingresar un título",
          life: 3000,
        });
      }
    },

    actualizarNoicia() {
      //Actualizar la noticia
      if (this.titulo && this.obtenerDatosComponentes()) {
        const ntc = {
          titulo: this.titulo,
          texto: this.textoObtenido,
          imagen: this.imagenObtenido,
          video: this.videoObtenido,
          fechaPublicacion: this.noticia.fechaPublicacion,
        };

        this.confirm.require({
          message: "Se editará la noticia",
          header: "Confirmación de Actualización",
          icon: "pi pi-check-circle",
          acceptLabel: "Sí",
          rejectLabel: "No",
          accept: () => {
            try {
              actualizarNoticiaFachada(ntc, this.noticia.id);
              window.location.reload();
            } catch (error) {
              console.log("No se pudo actualizar la noticia: ", error);
            }
          },
        });
      }
    },
  },
};
</script>

