  display: flex;
  flex-direction: column;
  align-items: center;
}
.titulo {
  align-items: flex-start;
  margin: 20px;
}

.noticias-admin {
  display: flex;
  align-items: center;
  flex-direction: column;
  margin-top: 30px;
  gap: 20px;
  width: 100%;
}

#titulo {
  color: black;
  font-weight: bold;
}

.opciones {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 30px;
}

.agregar {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  box-shadow: 0 0 10px grey;
  transform-style: preserve-3d;
  transform: rotateY(360deg);
  transition: transform 0.6s;
  animation: girar 1.6s ease-in-out;
}

@keyframes girar {
  100% {
    transform: rotateY(0deg);
  }
}

.agregado {
  display: flex;
